<style>
.container{
  display: flex;
  gap: 1.5rem;
  background: #C8102E;
  align-items:center;
  padding: 0.75rem;
  position: relative;
  font-weight: 350; 
  width: 100%;
  margin: 0 auto;
  overflow: auto;
}

.container svg{
   width: 30px;
   height: 30px;
}
.border_line{
  border-right: 1px solid #fff;
  height: 2rem;
  transform: rotate(15deg);
}
.category{
  display:flex;
  gap: 0.75rem;
  align-items: center;
}

@media (min-width: 1024px){
  .container{
    justify-content: center;
  }
}

.category svg{
  fill: white;
}

.category.u-category-select svg{
  fill: #ffe452;
}

.category.u-category-select {
  font-weight: bold;
  color:#ffe452;
}

.midiv:hover{
  color:#ffe452;}

.midiv:hover svg{
  fill: #ffe452;
  }
  .category .center{
    display: flex ;
    flex-direction:column;
    justify-content: center;
    align-items: center;  
    height: 3rem;
    /* width: 3rem; */
    text-align: center;
    gap: 3px;
  }

  .home{
  width: 1.8rem;
  height: 1.8rem;
 }

@media(max-width: 1023px){
  .category svg {
    width: 15px;
    height: 15px;
  }
  .category .mobile {
    width: 34px;
    height: 15px;
  }
  .category .text{
    font-size: 0.65rem;
  }
  .category .center{
    display: flex ;
    flex-direction:column;
    justify-content: center;
    align-items: center;  
    height: 3rem;
    width: 3rem;
    text-align: center;
  }
  
  .category .mobie{
    width: 24px;
    height: 20px;
  }


}
</style>